#!/usr/bin/env node

(function() {
  var cli       = require('cli');
  var closecss  = require('./index');
   
  //check if has been included by another module
  if(!module.parent) {
    cli.enable('version', 'status', 'glob');

    cli.parse({
      input: [ 'i', 'Source path to process close files', 'file', './src'], 
      output: [ 'o', 'Destination path for compiled CSS ouptu', 'file', './dest'], 
    });
  
    cli.main(function(args, options) {
      var progress = 0;
      var numfiles = 0;
      console.log(args, options);

      cli.spinner('Getting closer...');
      setTimeout(function(){
        cli.spinner('CLOSE ENOUGH!!', true);
      },3000);
    });
  }
}());
