var borderRadius = ['rounded', 'curved', 'squared'];

var randomBorderRadius = function (border){
  switch (border){
    case 'rounded':
      return '50%';
      break;
    case 'curved':
      return '25%';
      break;
    case 'squared':
      return '10%';
      break;
  }
};

module.exports = randomBorderRadius;
