var weights = ['normal', 'bold', 'light', 'medium', 'heavy'];

var randomWeight = function(weight){
  switch(weight){
    case 'normal':
      return 'normal';
      break;
    case 'bold':
      return 'bold';
      break;
    case 'light':
      return (Math.floor(Math.random() * 3 + 1) * 100) + '';
      break;
    case 'medium':
      return (Math.floor(Math.random() * 3 + 4) * 100) + '';
      break;
    case 'heavy':
      return (Math.floor(Math.random() * 3 + 7) * 100) + '';
      break;
  }
};

module.exports = randomWeight;
